import React, { useState } from "react";
import "../index.css";

import Contact from "./contact";
import About from "./about";
import Portfolio from "./portfolio";
import Resume from "./resume";

export default function Header() {
  const [about, setAbout] = useState(false);
  const [portfolio, setPortfolio] = useState(false);
  const [contact, setContact] = useState(false);
  const [resume, setResume] = useState(false);
  const [underline, setUnderline] = useState(false);

  const toggleAbout = () => setAbout(!about);
  const togglePortfolio = () => setPortfolio(!portfolio);
  const toggleContact = () => setContact(!contact);
  const toggleResume = () => setResume(!resume);
  const toggleUnderline = () => setUnderline(!underline);

  let linkStyle = underline ? "underline" : "";

  return (
    <>
      <div className="header">
        <h1>Robert Girardin | Front End Developer</h1>
        <div className="links">
          <div
            className={e =>
              underline && e.target.id === "about" ? linkStyle : undefined
            }
            onClick={() => {
              toggleAbout();
              setContact(false);
              setPortfolio(false);
              setResume(false);
            }}
            onMouseEnter={() => toggleUnderline()}
            onMouseLeave={() => toggleUnderline()}
          >
            <h1 id="about">About</h1>
          </div>
          <div
            className={portfolio ? "overline" : linkStyle}
            onClick={() => {
              togglePortfolio();
              setContact(false);
              setAbout(false);
              setResume(false);
            }}
            onMouseEnter={toggleUnderline}
            onMouseLeave={toggleUnderline}
          >
            <h1>Portfolio</h1>
          </div>
          <div
            className={resume ? "overline" : linkStyle}
            onClick={() => {
              toggleResume();
              setContact(false);
              setAbout(false);
              setPortfolio(false);
            }}
            onMouseEnter={toggleUnderline}
            onMouseLeave={toggleUnderline}
          >
            <h1>Resume</h1>
          </div>

          <div
            className={contact ? "overline" : linkStyle}
            onClick={() => {
              toggleContact();
              setAbout(false);
              setPortfolio(false);
              setResume(false);
            }}
            onMouseEnter={toggleUnderline}
            onMouseLeave={toggleUnderline}
          >
            <h1>Contact</h1>
          </div>
        </div>
      </div>
      <div className="spacer" />
      <div className="container">
        {contact && <Contact />}
        {about && <About />}
        {portfolio && <Portfolio />}
        {resume && <Resume />}
      </div>
    </>
  );
}
