import React, { useState } from "react";
import "../index.css";

import Contact from "./contact";
import About from "./about";
import Portfolio from "./portfolio";
import Resume from "./resume";

export default function Header() {
  const [about, setAbout] = useState(false);
  const [portfolio, setPortfolio] = useState(false);
  const [contact, setContact] = useState(false);
  const [resume, setResume] = useState(false);
  const [mouseTarget, setTarget] = useState("");
  const [hovered, setHovered] = useState(false);
  const [content, setContent] = useState("");

  const toggleAbout = () => !about && setAbout(true);
  const togglePortfolio = () => !portfolio && setPortfolio(true);
  const toggleContact = () => !contact && setContact(true);
  const toggleResume = () => !resume && setResume(true);

  const underline = hovered ? { textDecoration: "underline" } : null;

  const links = ["About", "Portfolio", "Resume", "Contact"];

  function renderSwitch(param) {
    switch (param) {
      case "About":
        return <About />;
      case "Portfolio":
        return <Portfolio />;
      case "Resume":
        return <Resume />;
      case "Contact":
        return <Contact />;
      default:
        return null;
    }
  }

  return (
    <>
      <div className="header">
        <h1>Robert Girardin | Front End Developer</h1>
        <div className="links">
          {links.map((item) => (
            <div
              onClick={() => {
                setContent(item);
              }}
            >
              <h1 className="linkItem">{item}</h1>
            </div>
          ))}
        </div>
      </div>
      <div className="spacer" />
      <div className="container">{renderSwitch(content)}</div>
    </>
  );
}

