import React, { useState } from "react";
import "../index.css";

import Contact from "./contact";
import About from "./about";
import Portfolio from "./portfolio";
import Resume from "./resume";

export default function Header() {
  const [about, setAbout] = useState(false);
  const [portfolio, setPortfolio] = useState(false);
  const [contact, setContact] = useState(false);
  const [resume, setResume] = useState(false);
  const [underline, setUnderline] = useState(false);
  const [id, setId] = useState("");

  const toggleAbout = () => setAbout(!about);
  const togglePortfolio = () => setPortfolio(!portfolio);
  const toggleContact = () => setContact(!contact);
  const toggleResume = () => setResume(!resume);
  const toggleUnderline = () => setUnderline(!underline);

  async function idSetter(e) {
    const idSetter = await toggleUnderline();
    if (underline) {
      setId(e.target.id);
    } else if (!underline) {
      setId("");
    }
  }

  let linkStyle = underline ? id : "";

  return (
    <>
      <div className="header">
        <h1>Robert Girardin | Front End Developer</h1>
        <div className="links">
          <div
            id="aboutLink"
            className={about ? "overline" : linkStyle}
            onClick={() => {
              toggleAbout();
              setContact(false);
              setPortfolio(false);
              setResume(false);
            }}
            onMouseEnter={e => {
              toggleUnderline(e);
              idSetter(e);
            }}
            onMouseLeave={e => {
              toggleUnderline(e);
              idSetter(e);
            }}
          >
            <h1>About</h1>
          </div>
          <div
            id="portfolioLink"
            className={portfolio ? "overline" : linkStyle}
            onClick={() => {
              togglePortfolio();
              setContact(false);
              setAbout(false);
              setResume(false);
            }}
            onMouseEnter={e => toggleUnderline(e)}
            onMouseLeave={e => toggleUnderline(e)}
          >
            <h1>Portfolio</h1>
          </div>
          <div
            id="resumeLink"
            className={resume ? "overline" : linkStyle}
            onClick={() => {
              toggleResume();
              setContact(false);
              setAbout(false);
              setPortfolio(false);
            }}
            onMouseEnter={e => toggleUnderline(e)}
            onMouseLeave={e => toggleUnderline(e)}
          >
            <h1>Resume</h1>
          </div>

          <div
            id="contactLink"
            className={contact ? "overline" : linkStyle}
            onClick={() => {
              toggleContact();
              setAbout(false);
              setPortfolio(false);
              setResume(false);
            }}
            onMouseEnter={e => toggleUnderline(e)}
            onMouseLeave={e => toggleUnderline(e)}
          >
            <h1>Contact</h1>
          </div>
        </div>
      </div>
      <div className="spacer" />
      <div className="container">
        {contact && <Contact />}
        {about && <About />}
        {portfolio && <Portfolio />}
        {resume && <Resume />}
      </div>
    </>
  );
}
